const ProductDescription = ({ product }) => {
  return (
    <div className="bg-white rounded-lg shadow-md p-6 mb-6">
      <h2 className="text-3xl font-bold text-gray-900 mb-2">
        {product.brand} {product.model}
      </h2>
      <p className="text-3xl font-bold text-blue-600 mb-6">{product.price}€</p>
      
      <div className="space-y-3">
        <DetailRow label="CPU" value={product.cpu} />
        <DetailRow label="RAM" value={product.ram} />
        <DetailRow label="Sistema Operativo" value={product.os} />
        <DetailRow label="Resolución" value={product.displayResolution} />
        <DetailRow label="Batería" value={product.battery} />
        <DetailRow label="Cámaras" value={product.primaryCamera.join(', ')} />
        <DetailRow label="Dimensiones" value={product.dimentions} />
        <DetailRow label="Peso" value={`${product.weight}g`} />
      </div>
    </div>
  );
};

const DetailRow = ({ label, value }) => (
  <div className="flex justify-between py-2 border-b border-gray-200">
    <span className="text-gray-600 font-semibold">{label}:</span>
    <span className="text-gray-900">{value}</span>
  </div>
);

export default ProductDescription;
