import { useState } from 'react';
import Router from './utils/router';
import Header from './components/Header';
import ProductListPage from './pages/ProductListPage';
import ProductDetailPage from './pages/ProductDetailPage';
import { addToCart } from './services/api';

function App() {
  const [cartCount, setCartCount] = useState(() => {
    const saved = localStorage.getItem('cartCount');
    return saved ? parseInt(saved, 10) : 0;
  });

  const handleAddToCart = async (productData) => {
    try {
      const response = await addToCart(productData);
      console.log('üì¶ Respuesta completa del carrito:', response);

      let newCount;

      // Si la API devuelve un count v√°lido, usarlo
      if (response && typeof response.count === 'number') {
        newCount = response.count;
        console.log('‚úÖ Usando count de la API:', newCount);
      } else {
        // Si no, incrementar localmente como respaldo
        newCount = cartCount + 1;
        console.log('‚ö†Ô∏è API no devolvi√≥ count v√°lido, incrementando localmente:', newCount);
      }

      setCartCount(newCount);
      localStorage.setItem('cartCount', newCount.toString());

      alert(`‚úÖ Producto a√±adido al carrito\nTotal de productos: ${newCount}`);
    } catch (error) {
      console.error('‚ùå Error adding to cart:', error);
      alert('‚ùå Error al a√±adir el producto al carrito');
    }
  };

  return (
    <Router>
      {({ currentPath, navigate }) => {
        // REGEX MEJORADA: Ahora captura IDs con guiones, guiones bajos y cualquier car√°cter alfanum√©rico
        const productMatch = currentPath.match(/#\/product\/([a-zA-Z0-9_-]+)/);
        const productId = productMatch ? productMatch[1] : null;

        console.log('Current path:', currentPath);
        console.log('Product ID extracted:', productId);

        return (
          <div className="min-h-screen bg-gray-100">
            <Header cartCount={cartCount} />

            <main className="max-w-7xl mx-auto px-4 py-8">
              {productId ? (
                <ProductDetailPage 
                  productId={productId}
                  navigate={navigate}
                  onAddToCart={handleAddToCart}
                />
              ) : (
                <ProductListPage navigate={navigate} />
              )}
            </main>
          </div>
        );
      }}
    </Router>
  );
}

export default App;
