import cacheManager from './cache';

const API_BASE = 'https://itx-frontend-test.onrender.com/api';

export const getProducts = async () => {
  const cached = cacheManager.get('products');
  if (cached) {
    console.log('‚úÖ Usando productos desde cach√©');
    return cached;
  }
  
  console.log('üì° Obteniendo productos desde API');
  
  try {
    const response = await fetch(`${API_BASE}/product`);
    
    if (!response.ok) {
      throw new Error(`Error ${response.status}: ${response.statusText}`);
    }
    
    const data = await response.json();
    cacheManager.set('products', data);
    return data;
  } catch (error) {
    console.error('‚ùå Error obteniendo productos:', error);
    throw error;
  }
};

export const getProduct = async (id) => {
  const cached = cacheManager.get(`product-${id}`);
  if (cached) {
    console.log(`‚úÖ Usando producto ${id} desde cach√©`);
    return cached;
  }
  
  console.log(`üì° Obteniendo producto ${id} desde API`);
  
  try {
    const response = await fetch(`${API_BASE}/product/${id}`);
    
    console.log(`üìä Status de respuesta para ${id}:`, response.status);
    
    if (!response.ok) {
      const errorText = await response.text();
      console.error(`‚ùå Error response para ${id}:`, errorText);
      throw new Error(`Error ${response.status}: Producto no encontrado`);
    }
    
    const data = await response.json();
    console.log(`‚úÖ Producto ${id} obtenido:`, data);
    
    cacheManager.set(`product-${id}`, data);
    return data;
  } catch (error) {
    console.error(`‚ùå Error obteniendo producto ${id}:`, error);
    throw error;
  }
};

export const addToCart = async (productData) => {
  console.log('üõí A√±adiendo producto al carrito:', productData);
  
  try {
    const response = await fetch(`${API_BASE}/cart`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(productData)
    });
    
    console.log('üìä Status de respuesta del carrito:', response.status);
    
    if (!response.ok) {
      const errorText = await response.text();
      console.error('‚ùå Error response del carrito:', errorText);
      throw new Error(`Error ${response.status}: ${response.statusText}`);
    }
    
    const data = await response.json();
    console.log('‚úÖ Respuesta del carrito:', data);
    
    return data;
  } catch (error) {
    console.error('‚ùå Error en addToCart:', error);
    throw error;
  }
};
