import { useState, useEffect } from 'react';
import ProductImage from '../components/ProductImage';
import ProductDescription from '../components/ProductDescription';
import ProductActions from '../components/ProductActions';
import { getProduct } from '../services/api';

const ProductDetailPage = ({ productId, navigate, onAddToCart }) => {
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  
  useEffect(() => {
    const loadProduct = async () => {
      try {
        const data = await getProduct(productId);
        setProduct(data);
      } catch (error) {
        console.error('Error loading product:', error);
      } finally {
        setLoading(false);
      }
    };
    
    loadProduct();
  }, [productId]);
  
  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-xl text-gray-600">Cargando producto...</div>
      </div>
    );
  }
  
  if (!product) {
    return (
      <div className="text-center py-12">
        <p className="text-xl text-gray-600 mb-4">Producto no encontrado</p>
        <button
          onClick={() => navigate('#/')}
          className="text-blue-600 hover:underline"
        >
          Volver a la lista
        </button>
      </div>
    );
  }
  
  return (
    <div>
      <button
        onClick={() => navigate('#/')}
        className="mb-6 text-blue-600 hover:underline flex items-center space-x-2"
      >
        <span>‚Üê</span>
        <span>Volver a productos</span>
      </button>
      
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <ProductImage 
          imgUrl={product.imgUrl}
          brand={product.brand}
          model={product.model}
        />
        
        <div>
          <ProductDescription product={product} />
          <ProductActions product={product} onAddToCart={onAddToCart} />
        </div>
      </div>
    </div>
  );
};

export default ProductDetailPage;
